jQuery(function ($) {

    // 扫描按钮
    $('#rwwcl-scan-btn').on('click', function () {
        $(this).prop('disabled', true).text('Scanning...');

        $.post(ajaxurl, {
            action: 'rwwcl_scan_images',
            nonce: rwwcl_object.nonce
        }, function (response) {
            $('#rwwcl-scan-btn').prop('disabled', false).text('Scan Unoptimized Images');

            if (!response.success) {
                alert('Scan failed.');
                return;
            }

            alert('Found ' + response.data.count + ' unoptimized images.');

            $('#rwwcl-start-bulk-btn').prop('disabled', response.data.count == 0);

        });
    });

    // 批量优化按钮
    $('#rwwcl-start-bulk-btn').on('click', function () {
        $(this).prop('disabled', true).text('Optimizing...');

        $.post(ajaxurl, {
            action: 'rwwcl_bulk_optimize',
            nonce: rwwcl_object.nonce
        }, function (response) {
            if (!response.success) {
                alert('Optimization failed.');
                return;
            }

            alert('Optimization complete. Total optimized: ' + response.data.optimized);

            location.reload();
        });
    });

});
